def run
    puts "salut !"
end 

def count
    tab = fulltab.length
    puts "il y a #{tab} handle dans le tableaux"
end

def short
    tab = fulltab.sort_by(&:length)
    puts "le handle le plus cour est #{tab[0]}"
end

def letter
    tab5 = []
    fulltab.each do |i|
        if  i.size == 6       
            tab5.push(i)
        end
    end
    puts  "il y a #{tab5.length} handle avec 5 caracter "
    #names = fulltab.count {|i| i.length == 6}
end

def upper
    uppern = fulltab.count{|i| i[1].match?(/[A-Z]/)}
    puts uppern
end   

def az
    test = fulltab.map(&:downcase)
    puts "#{test.sort}"
end

def stol
    test = fulltab.sort_by(&:length)
    puts test 
end

def epenser
    y = 0
    fulltab.each do |i|
        
        if i != "@epenser"   
            y = y + 1 
        end
    end
    puts "E-pense est a la position #{y}"

    #position = a.index("@epenser")
    #puts "@epenser est a la #{position} places"
end 

def last  
    
    nbtab = []
    b = Hash.new(0)

    fulltab.each do |i|
        nbtab.push(i.length - 1)   
    end

    nbtab.each do |i|
        b[i] += 1
    end

    b.sort.each do |i, j|
        puts "il y a #{j} handle avec #{i} caracter"
    end

end

def choise
    puts "tu veut quel programme ?"
    puts "1 pour le nombre d'entrée"
    puts "2 le plus cour " 
    puts "3 pour les handle contenant 5 caractères"
    puts "4 pour les handle avec maj"
    puts "5 pour trier de a-z"
    puts "6 pour trier les handles par taille"
    puts "7 pour trouver e-penser"
    puts "8 pour voir la repartition des caracter"
    print ">>>"
    input = gets.chomp.to_i
    if input === 1
        count
    elsif input === 2
        short
    elsif input === 3
        letter
    elsif input === 4
        upper
    elsif input === 5
        az 
    elsif input === 6
        stol
    elsif input === 7
        epenser
    elsif input === 8
        last    
    end
end

def perform
    fulltab
    run
    choise
end

